import * as React from 'react';
import "./Form.css"
import TableContainer from "./TableContainer"



export interface formState {
	value: string;
	showData: boolean;
	teamUrls: string[];
}

export interface scrapeResponse {
	data:string[][][];
}

class Form extends React.Component<{}, formState> {
	constructor(props:any) {
	  	super(props);
	  	this.state = {
	  		value: '',
	  		showData: false,
	  		data: []
	  	}
	  	this.handleChange = this.handleChange.bind(this);
	  	this.handleSubmit = this.handleSubmit.bind(this);
	  	this.scrape = this.scrape.bind(this);
	}

	async scrape () {
		await fetch('http://localhost:2999/get_teams', {
  			method: 'POST',
  			headers: {
  				'Access-Control-Allow-Origin': '*',
  				'Accept': 'application/json',
    			'Content-Type': 'application/json'
  			},
  			body: JSON.stringify({"value": this.state.value})
  		})
  			.then(async (response) => {
  				if (response.ok) {
  					await response.json().then(json => {
  						console.log("FROM THEN: " + JSON.stringify(json))
  						this.setState({
  							showData: true,
  							data: json.data
  						})
  					})
  				}
  			})
			.catch((e) => console.log("ERROR: " + e))
	}

	handleChange(event:any) {
		this.setState({value: event.target.value});
		// console.log(this.state)
	}

	async handleSubmit(event:any) {
		event.persist()
		// console.log(this.state)
		// await this.scrape();
		this.setState({
  							showData: true,
  						})
    	event.preventDefault();
	}


	generateTables(data:scrapeResponse) {
		this.setState({
			value: this.state.value,
			showData: true,
			data: data.data
		})
	}







	render() {	
		return(
			<div className="FormData">
				<div className="Form">
					<form onSubmit={this.handleSubmit}>
			        	<p>Enter the school's athletics URL:</p>
			        	<input type="text" value={this.state.value} onChange={this.handleChange} />
			        	<br></br>
			        	<input type="submit" value="Submit"/>
			      	</form>
			    </div>
			    <div className="Tables">
			    	{ this.state.showData ? <TableContainer rawData={this.state.data}/> : null }
			    </div>
			</div>
		);
	}
}



export default Form;
