import * as React from 'react';
import "./Form.css"
import "./TableContainer"



export interface formState {
	value: string;
	showData: boolean;
	data: string[][][];
}

export interface scrapeResponse {
	data:string[][][];
}

class Form extends React.Component<{}, formState> {
	constructor(props:any) {
	  	super(props);
	  	this.state = {
	  		value: '',
	  		showData: false,
	  		data: []
	  	}
	  	this.handleChange = this.handleChange.bind(this);
	  	this.handleSubmit = this.handleSubmit.bind(this);
	  	this.scrape = this.scrape.bind(this);
	}

	async scrape () {
		await fetch('http://localhost:2999/start_scrape', {
  			method: 'POST',
  			headers: {
  				'Access-Control-Allow-Origin': '*',
  				'Accept': 'application/json',
    			'Content-Type': 'application/json'
  			},
  			body: JSON.stringify({"value": this.state.value})
  		})
  			.then((response) => {
  				return response.json()
  			})
			.catch((e) => console.log("ERROR: " + e))
	}

	handleChange(event:any) {
		this.setState({value: event.target.value});
		// console.log(this.state)
	}

	async handleSubmit(event:any) {
		event.persist()
		// console.log(this.state)
		console.log("api response: ", await this.scrape())
    	event.preventDefault();
	}


	generateTables(data:scrapeResponse) {
		this.setState({
			value: this.state.value,
			showData: true,
			data: data.data
		})
	}







	render() {	
		return(
			<div className="FormData">
				<div className="Form">
					<form onSubmit={this.handleSubmit}>
			        	<p>Enter the school's athletics URL:</p>
			        	<input type="text" value={this.state.value} onChange={this.handleChange} />
			        	<br></br>
			        	<input type="submit" value="Submit"/>
			      	</form>
			    </div>

			</div>
		);
	}
}


// <div className="Tables">
// 			    	{ this.state.showData ? <TableContainer input={this.state.data}/> : null }
// 			    </div>
export default Form;
